import React from 'react';
import { connect } from 'react-redux';

import FormInput from '../form-input/form-input.component';
import CustomButton from '../custom-button/custom-button.component';

import { auth, signInWithGoogle } from '../../firebase/firebase.utils';
import { googleSignInStart } from '../../redux/user/user.actions'

import './sign-in.styles.scss';

class SignIn extends React.Component {
	constructor(props) {
		super(props)
		this.state = {
			email: '',
			password: ''
		}
	}

	handleSubmit = async (event) => {
		event.preventDefault();

		const {email, password} = this.state

		try {
			await auth.signInWithEmailAndPassword(email,password)
			this.setState({ email: '', password: ''})
		} catch (error) {
			console.log(error)
		}
	}

	handleChange = (event) => {
		const { value, name } = event.target;
		this.setState({ [name]: value })
	}

	render() {
		const { googleSignInStart } = this.props;
		const { email, password } = this.state;
		return(
			<div className='sign-in'>
				<h2>I already have an account</h2>
				<span>Sign in with your email and password</span>

				<form onSubmit={this.handleSubmit}>
					<FormInput label='email' handleChange={this.handleChange} name="email" type="email" value={email} required/>
					<FormInput label='password' handleChange={this.handleChange} name="password" type="password" value={password} required/>
					<div className='buttons'>
						<CustomButton type="submit"> Sign in </CustomButton>
						<CustomButton onClick={googleSignInStart} isGoogleSignIn> Sign in with Google </CustomButton>
					</div>
				</form>
			</div>
		)
	}

}

const mapDispatchToProps = dispatch => ({
	googleSignInStart: () => dispatch(googleSignInStart())
})

export default connect(null,mapDispatchToProps)(SignIn);
